/* testing for equality v2 may have more properties than v1 but not less */
function objectEquals(v1, v2) {

    //wait();

    if (typeof(v1) !== typeof(v2)) {
         console.log("property typeof(v1) !==typeof(v2)" + typeof(v1)  + typeof(v2) + "==>" + v1 + "!=" + v2);
        return false;
    }

    if (typeof(v1) === "function") {
        return v1.toString() === v2.toString();
    }

    if (v1 instanceof Object && v2 instanceof Object) {

        var r = true;
        for (k in v1) {

            r = objectEquals(v1[k], v2[k]);



            if (!r) {
                 tests["property " + k.toString() + "==>" + v1[k] + "!=" + v2[k]] = false;
                 console.log("property " + k.toString() + "==>" + v1[k] + "!=" + v2[k]);
                 postman.setGlobalVariable("unequal" +  k.toString(), v1[k] + "!=" + v2[k]);

                return false;
            }
            //tests["property " + k.toString() + "==>" + v1[k] + "!=" + v2[k]] = true;
        }
        return true;
    } else {
        return v1 === v2;
    }
}

